const reducer = (state = [], action) => {
    // a reduce is a function or more specifically it is equal to a function that accepts the state
    // if(action.type === 'CREATE') {
    //     return action;
    // }

    switch (action.type) {
        case 'FETCH_ALL':
            return state; 
        case 'CREATE':
            return state; 
        default:
            return state;
    }
}
